import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Switch } from 'react-bootstrap';
import './App.css';

function App() {
  const [status, setStatus] = useState(null);

  useEffect(() => {
    fetchStatus();
  }, []);

  const fetchStatus = async () => {
    const response = await fetch('http://localhost:5000/api/status');
    const data = await response.json();
    setStatus(data);
  };

  const toggleLock = async () => {
    const response = await fetch('http://localhost:5000/api/toggle', { method: 'POST' });
    const data = await response.json();
    setStatus(data);
  };

  return (
    <Container className="App">
      <Row className="align-items-center">
        <Col>
          <p>Door lock status: {status && (status.locked ? 'Locked' : 'Unlocked')}</p>
        </Col>
        <Col>
          <Form>
            <Form.Check
              type="switch"
              id="door-lock-switch"
              label="Toggle Lock"
              checked={status && status.locked}
              onChange={toggleLock}
              custom
            />
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
