import React, { useState, useEffect } from 'react';
import mqtt from 'mqtt';

const MQTT_BROKER = '1232:00'; // แทนที่ด้วย MQTT Broker ที่คุณใช้
const MQTT_TOPIC = '123'; // แทนที่ด้วย topic ที่คุณใช้สำหรับสถานะของสวิตช์ปลดล็อคประตู

function App() {
  const [lockStatus, setLockStatus] = useState('Unknown');

  useEffect(() => {
    const client = mqtt.connect(MQTT_BROKER);

    client.on('connect', () => {
      console.log('Connected to MQTT Broker');
      client.subscribe(MQTT_TOPIC);
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        setLockStatus(message.toString());
      }
    });

    return () => {
      client.end();
    };
  }, []);

  return (
    <div >
      <header >

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
